CREATE TABLE IF NOT EXISTS communitystats (id VARCHAR(512) PRIMARY KEY, score DOUBLE PRECISION NOT NULL, baseurl VARCHAR(255) NOT NULL, nsfw BOOLEAN NOT NULL, counts JSON NOT NULL, "isSuspicious" BOOLEAN NOT NULL, "name" VARCHAR(255) NOT NULL, published BIGINT NOT NULL, "time" BIGINT NOT NULL, title VARCHAR(255) NOT NULL, url VARCHAR(512) NOT NULL, "desc" TEXT NOT NULL, icon TEXT NULL, banner TEXT NULL);